    square_definition


function tree_map(square,tree)
  {
  return map (function(sub_tree)
      {
      if(is_empty_list(sub_tree))
        return [];
      else if(is_pair(sub_tree))
        return tree_map(square,sub_tree);
      else
        return square(sub_tree);
      },tree);
        }

function square_tree(tree) 
  {
   return tree_map(square,tree);
  }

