    example_2.33


function append(list1,list2) {
   if (is_empty_list(list1))
      return list2;
   else {
       return pair(head(list1),append(tail(list1),list2));
   }
}

function map(fun,items) {
   if (is_empty_list(items)) {
      return [];
   } else {
      return pair(fun(head(items)),map(fun,tail(items)));
   }
}

function subsets(s) {
   if (is_empty_list(s)) {
      return list([]);
   } else {
      var rest = subsets(tail(s));
      return append(rest,map(function(x) {return pair(head(s),x);},rest));
   }
}






